/* Consider a database for a library management system. The database should keep track of 
books, authors, and users. Each book can have one or more authors. Users can borrow multiple 
books, and each book can be borrowed by multiple users. */

DROP DATABASE IF EXISTS library;

CREATE DATABASE library;

USE library;

CREATE TABLE authors (
    id INT PRIMARY KEY AUTO_INCREMENT,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    nationality VARCHAR(50),
    is_active BOOLEAN DEFAULT FALSE,
    debut DATE
);

CREATE TABLE books (
    id INT PRIMARY KEY AUTO_INCREMENT,
    author_id INT NOT NULL,
    title VARCHAR(100) NOT NULL,
    description TEXT,
    serial_number VARCHAR(15) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    publish_date DATE,
    FOREIGN KEY (author_id) 
		REFERENCES authors(id) 
        ON UPDATE CASCADE ON DELETE CASCADE
);

CREATE TABLE users (
    id INT PRIMARY KEY AUTO_INCREMENT,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    gender ENUM('M', 'F'),
    email VARCHAR(100),
    address VARCHAR(100) NOT NULL
);

CREATE TABLE borrowed_books (
    id INT PRIMARY KEY AUTO_INCREMENT,
    user_id INT NOT NULL,
    book_id INT NOT NULL,
    borrow_date DATE,
    return_date DATE,
    FOREIGN KEY (user_id) 
		REFERENCES users(id) 
        ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (book_id) 
		REFERENCES books(id) 
		ON UPDATE CASCADE ON DELETE CASCADE
);

CREATE TABLE book_authors (
    author_id INT NOT NULL,
    book_id INT NOT NULL,
    PRIMARY KEY (author_id, book_id),
    FOREIGN KEY (author_id) 
		REFERENCES authors(id) 
        ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (book_id) 
		REFERENCES books(id) 
		ON UPDATE CASCADE ON DELETE CASCADE
);






-- authors
SELECT 
	CONCAT_WS(' ', first_name, last_name) AS full_name
FROM 
	authors
WHERE 
	nationality = 'China'
ORDER BY 
	full_name;

SELECT 
	nationality,
	COUNT(is_active)
FROM 
	authors
WHERE 
	is_active = true
GROUP BY 
	nationality;

SELECT 
	nationality,
	COUNT(*) AS total
FROM 
	authors
GROUP BY 
	nationality
HAVING 
	total > 2
ORDER BY
	total DESC;

-- books
SELECT 
	title,
    price,
    publish_date
FROM 
	books
WHERE 
	publish_date > '2024-01-01'
ORDER BY 
	price,
    title;

SELECT 
	AVG(price) AS avg_price
FROM
	books;
    
SELECT 
	MAX(price) AS max_price
FROM
	books;
    
SELECT
	SUM(price) AS total_price
FROM 
	books;

-- users
SELECT 
	CONCAT_WS(' ', first_name, last_name) AS full_name,
    address 
FROM
	users 
WHERE 
	gender <> 'M'
ORDER BY
	full_name;
    
SELECT 
	gender,
    COUNT(*) AS total_count
FROM
	users
GROUP BY 
	gender;
    
-- borrowed_books
SELECT 
	user_id,
	book_id
FROM
	borrowed_books
WHERE
	borrow_date BETWEEN '2023-10-01' AND '2023-12-31';
    
SELECT 
	user_id,
	book_id
FROM
	borrowed_books
WHERE
	borrow_date > '2023-10-01' AND 
	return_date < '2023-12-31';

-- joins
SELECT 
	CONCAT_WS(' ', a.first_name, a.last_name) AS full_name,
    a.nationality,
    b.title,
    b.description,
    b.publish_date
FROM
	authors a 
INNER JOIN books b ON
	a.id = b.author_id
WHERE
	a.nationality IS NOT NULL AND b.publish_date IS NOT NULL
ORDER BY 
	b.publish_date DESC,
    full_name;

SELECT 	
	a.nationality,
    SUM(b.price) AS total_price
FROM
	authors a
INNER JOIN books b ON
	a.id = b.author_id
WHERE
	a.is_active = true AND
    a.nationality IS NOT NULL
GROUP BY 
	a.nationality
ORDER BY 
	total_price DESC;
	
SELECT 
	CONCAT_WS(' ', u.first_name, u.last_name) AS user_full_name,
    u.address,
    b.title,
    ROUND(b.price) AS book_price,
    bb.borrow_date,
    bb.return_date,
    CONCAT_WS(' ', a.first_name, a.last_name) AS author_full_name
FROM 	
	users u
LEFT JOIN borrowed_books bb ON
	u.id = bb.user_id
LEFT JOIN books b ON
	bb.book_id = b.id
LEFT JOIN authors a ON
	b.author_id = a.id
WHERE
	b.price > 12.50 AND
    bb.borrow_date BETWEEN '2024-01-01' AND '2024-03-15'
ORDER BY 
	b.price DESC,
    user_full_name,
    b.title;

SELECT 
	u.gender,
    ROUND(AVG(b.price), 2) AS avg_price
FROM
	users u
INNER JOIN borrowed_books bb ON
	u.id = bb.user_id
INNER JOIN  books b ON
	bb.book_id = b.id
GROUP BY 
	u.gender
HAVING 
	gender = 'M';

SELECT 
	CONCAT_WS(' ', a.first_name, a.last_name) AS full_name,
    COUNT(b.author_id) AS total_books
FROM
	authors a 
INNER JOIN books b ON
	a.id = b.author_id
GROUP BY
	full_name
HAVING
	total_books > 1
ORDER BY 
	total_books DESC;

-- views
CREATE VIEW view_books_info AS 
SELECT 
	id,
    title,
    serial_number,
    price
FROM
	books
WHERE 
	title LIKE 'M%'
ORDER BY
	price DESC;
    
SELECT * FROM view_books_info;

CREATE VIEW view_general_library_info AS
SELECT 
	CONCAT_WS(' ', a.first_name, a.last_name) AS author_full_name,
    b.title,
    b.serial_number,
    CONCAT_WS(' ', u.first_name, u.last_name) AS user_full_name,
    u.address,
    bb.borrow_date
FROM 
	authors a
INNER JOIN books b ON
	a.id = b.author_id
INNER JOIN borrowed_books bb ON
	b.id = bb.book_id
INNER JOIN users u ON
	bb.user_id = u.id
ORDER BY 
	author_full_name,
    user_full_name
LIMIT 30;

SELECT * FROM view_general_library_info;